INSERT INTO `judge` VALUES (351, 1, '阶级斗争受社会历史条件的制约，它的作用是历史的、变化的。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (352, 1, '马克思主义的阶级分析法是认识全部人类社会历史的科学方法。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (353, 1, '坚持阶级分析方法，就是运用马克思主义的阶级和阶级斗争观点去观察和认识阶级社会的社会历史现象。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (354, 1, '阶级分析方法要求全面地、动态地分析阶级状况。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (355, 1, '“革命是历史的火车头”意思是说社会革命始终是“社会进步和政治进步的强大推动力”。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (356, 1, '社会革命是实现社会形态更替的重要手段和决定性环节。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (357, 1, '社会革命能够充分发挥人民群众创造历史的积极性和伟大作用。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (358, 1, '无产阶级革命将会为消除阶级对抗，并充分利用全人类的文明成果促进社会全面进步创造条件。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (359, 1, '马克思主义重视革命的伟大作用，也就否认了改良作为革命的一种补充手段。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (360, 1, '改革适用于解决现存的社会基本制度问题，把生产力从已不能容纳它的旧的生产关系中解放出来。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (361, 1, '改革是同一种社会形态发展过程中的量变和部分质变，是推动社会发展的重要动力。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (362, 1, '社会主义改革是社会主义制度的自我完善和自我发展。', '对', '错', '对');
INSERT INTO `judge` VALUES (363, 1, '科学技术革命是推动经济和社会发展的强大杠杆。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (364, 1, '科学技术经常“表现为异己的、敌对的和统治的权力”。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (365, 1, '唯物史观和唯心史观的对立，在历史创造者问题上表现为群众史观与英雄史观的对立。', '对', '错', '对');
INSERT INTO `judge` VALUES (366, 1, '英雄史观从社会意识决定社会存在的基本前提出发，否认物质资料的生产方式是社会发展的决定力量，抹煞人民群众的历史作用，宣扬少数英雄人物创造历史。', '对', '错', '对');
INSERT INTO `judge` VALUES (367, 1, '现实的人及其活动是社会历史存在和发展的前提。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (368, 1, '现实的人是基于自身需要和社会需要而从事一定实践活动的、处于一定社会关系中的、具有能动性的人。', '对', '错', '对');
INSERT INTO `judge` VALUES (369, 1, '人们要从一定的社会关系中包括阶级关系中去认识和把握一定群体和个人的本质和作用。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (370, 1, '在社会历史发展过程中，英雄人物和人民群众共同起着决定性的作用。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (371, 1, '人民群众是一个历史范畴。在不同历史时期，人民群众有着不同的内容，包含着不同的阶级、阶层和集团。', '对', '错', '对');
INSERT INTO `judge` VALUES (372, 1, '港澳台同胞和海外华侨都属于人民群众的范畴。', '对', '错', '错');
INSERT INTO `judge` VALUES (373, 1, '人民群众的总体意愿和行动代表了历史发展的方向，人民群众的社会实践最终决定历史发展的结局。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (374, 1, '人民群众的生活、实践活动是一切精神财富、精神产品形成和发展的源泉。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (375, 1, '人民群众只能间接地参与社会精神财富的创造。', '对', '错', '错');
INSERT INTO `judge` VALUES (376, 1, '人民群众也是推动中国改革开放的决定力量。', '对', '错', '对');
INSERT INTO `judge` VALUES (377, 1, '生产关系的变革，社会制度的更替，最终决定于生产力的发展，但却不会随着生产力的发展自发地实现和完成，而必须借助人民群众的力量。', '对', '错', '对');
INSERT INTO `judge` VALUES (378, 1, '人民群众中蕴藏着无穷的力量，人民群众创造历史的能力是无限的。', '对', '错', '错');
INSERT INTO `judge` VALUES (379, 1, '个人在历史上的作用存在差别，有些人的作用大些，有些人的作用小些，有些人不起任何作用。', '对', '错', '错');
INSERT INTO `judge` VALUES (380, 1, '社会历史发展是无数个人合力作用的结果。', '对', '错', '对');
INSERT INTO `judge` VALUES (381, 1, '唯物史观既明确了人民群众是历史的创造者，也不否认个人在历史上的作用。', '对', '错', '对');
INSERT INTO `judge` VALUES (382, 1, '马克思主义说人民群众是历史的创造者，也就否定了英雄人物的历史作用。', '对', '错', '错');
INSERT INTO `judge` VALUES (383, 1, '无论历史人物在历史上发挥什么样的作用，都要受到社会发展客观规律的制约，而不能决定和改变历史发展的总进程和总方向。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (384, 1, '要从必然性或偶然性中理解历史人物的作用。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (385, 1, '任何历史人物的出现，包括杰出的政治人物的出现，都体现了必然性与偶然性的统一。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (386, 1, '历史人物的作用性质取决于他们的思想、行为是否符合社会发展规律，是否符合人民群众的意愿。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (387, 1, '只有顺应历史发展的要求和人民群众的意愿，历史人物才能起到推动社会前进的积极作用。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (388, 1, '评价历史人物必须坚持历史分析方法和阶级分析方法的统一。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (389, 1, '历史分析方法要求从特定的历史背景出发，根据当时的历史条件，对历史人物的是非功过进行具体的、全面的考察。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (390, 1, '阶级分析方法要求把历史人物置于一定的阶级关系中，同他所属的阶级联系起来加以考察和评价。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (391, 1, '必须看到，当今困扰世界的很多问题，都是经济全球化造成的。', '对', '错', '错');
INSERT INTO `judge` VALUES (392, 1, '从本质上讲，经济全球化是生产力发展和社会化大生产的必然要求。', '对', '错', '对');
INSERT INTO `judge` VALUES (393, 1, '从历史发展的角度看，资本主义生产资料所有制是不断演进和变化的。', '对', '错', '对');
INSERT INTO `judge` VALUES (394, 1, '从人类社会发展的长河看，资本主义终究要被社会主义所取代，这是历史发展的基本趋势。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (395, 1, '大银行可通过购买工业企业股票等方式参与和控制工业企业，把它们控制在自己手中。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (396, 1, '大银行要派人充当企业的董事或经理，实现银行与企业人事上的结合，直接掌握和控制企业。银行与工业企业之间的关系日益密切，促使银行垄断资本和工业垄断资本逐渐融合起来。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (397, 1, '大银行与工业企业之间逐渐形成了较为固定的金融关系，银行可借此掌握了企业资金往来和经营情况，能对企业进行及时的有效的监督和控制，甚至左右企业的命运。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (398, 1, '当代资本主义的新变化，不可否认是人类历史进步的表现，也改变资本主义生产关系的实质。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (399, 1, '当代资本主义发生的变化从根本上说是人类社会发展一般规律和资本主义经济规律作用的结果。', '对', '错', '对');
INSERT INTO `judge` VALUES (400, 1, '当代资本主义发生的变化是在资本主义制度基本框架内的变化，也意味着资本主义生产关系的根本性质发生了变化。', '对', '错', '错');
INSERT INTO `judge` VALUES (401, 1, '当代资本主义发生地变化意味着资本主义生产关系的根本性质发生了变化。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (402, 1, '当生产集中达到一定高度时，就具备了产生垄断的可能性。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (403, 1, '第二次世界大战后，发达资本主义国家的相对稳定和发展，表明现代资本主义固有的矛盾正在消失。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (404, 1, '二战后，出现了国家垄断资本主义，所以资本主义的经济性质已经改变。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (405, 1, '二战后，市场机制依然是唯一的经济调节机制。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (406, 1, '法人资本所有制是资本主义生产资料所有制发展的新形式，是自然人股东化的产物。', '对', '错', '错');
INSERT INTO `judge` VALUES (407, 1, '法人资本所有制是资本主义生产资料所有制发展的新形式。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (408, 1, '国家和私人共有的垄断资本，即国有垄断资本和私人垄断资本在一个企业内部的结合。在这种形式中，国家以资本所有者的身份和作为资本所有者的垄断组织合作经营企业。其形式是股份公司。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (409, 1, '国家和私人密切联系的垄断资本，即国家垄断资本与私人垄断资本在社会范围内的结合。在这种形式中，私人垄断资本是资本运动的主体，私人垄断资本与国家垄断资本的结合，并不改变私人垄断资本的占有形式，改变的却是资', '对', '错', '对');
INSERT INTO `judge` VALUES (410, 1, '国家垄断资本从垄断资本的整体利益、长远利益和全局观点考虑资本主义经济发展问题，从而在相当程度上克服了私人垄断资本只顾眼前利益和局部利益的局限性。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (411, 1, '国家垄断资本的实力雄厚，从而在相当程度上克服了私人垄断资本数量相对不足的矛盾。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (412, 1, '国家垄断资本对经济的宏观调节和计划管理，也在相当程度上克服或抑制了私人垄断资本的无政府状态和盲目性。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (413, 1, '国家垄断资本主义产生和发展的根本原因，就在于资本主义基本矛盾的消失和转化。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (414, 1, '国家垄断资本主义的宏观经济调节本身有着不可克服的内在矛盾。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (415, 1, '国家垄断资本主义的实质是通过国家的力量，保证垄断资产阶级获得高额垄断利润，以维护资本主义制度。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (416, 1, '国家垄断资本主义就是国家政权与私人垄断资本相结合而形成的一种垄断资本主义，其实质是垄断资本利用国家政权的力量，以维护其垄断统治并保证获得稳定的利润。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (417, 1, '国家垄断资本主义是社会主义的最充分的物质准备，是社会主义的前阶。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (418, 1, '国家垄断资本主义是以私人垄断资本为基础的，并为其获取高额垄断利润服务的。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (419, 1, '国家垄断资本主义是资本社会化的最高形式，因而在相当程度上克服了私人垄断资本社会化程度较低的局限性。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (420, 1, '国家垄断资本主义是资本主义国家和垄断资本相结合而形成的一种资本主义。因此，由于国家和垄断资本结合的情况不同，便形成了国家垄断资本主义的不同具体形式。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (421, 1, '国家垄断资本主义无论采取何种形式，其实质都是私人垄断资本利用国家机器来为其发展服务的手段，是私人垄断资本为了维护垄断统治和获取高额垄断利润而和国家政权相结合的一种垄断资本主义形式，是资产阶级国家在直接', '对', '错', '对');
INSERT INTO `judge` VALUES (422, 1, '国有垄断资本，即国家作为垄断资本家的总代表直接掌握的垄断资本。其中国家投资是形成国有垄断资本的主要途径，而国有企业是国有垄断资本的组织形式。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (423, 1, '金融资本既不是银行垄断资本，也不是工业垄断资本，而是在银行垄断资本和工业垄断资本的基础上所形成的一种新的资本形态。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (424, 1, '尽管垄断利润远远高于平均利润，但是，就其来源来说，依然是工人阶级创造的剩余价值和其他劳动者创造的一部分价值。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (425, 1, '近年来，西方社会不断出现不同群体、阶层的矛盾与冲突，甚至社会动荡。', '对', '错', '对');
INSERT INTO `judge` VALUES (426, 1, '近年来移植西式民主的国家陷入动荡，西方国家本身也出现了某些治理危机，暴露出西式民主的弊端和局限。', '对', '错', '对');
INSERT INTO `judge` VALUES (427, 1, '经济全球化的过程是生产社会化程度不断提高的过程。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (428, 1, '经济全球化的最终结果将不单指经济生活的全球化，而且也包括政治文化和社会生活的全球化。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (429, 1, '经济全球化会缩小发达国家与发展中国家之间的差距。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (430, 1, '经济全球化就是全球资本主义化。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (431, 1, '经济全球化是当代资本主义主导下进行的，是由美国为首的发达资本主义国家积极推动起来的。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (432, 1, '经济全球化是生产社会化及经济国际化高度发展在时间和空间上的多维度拓展，因而反映了科学技术进步和人类社会生产力发展的客观要求。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (433, 1, '经济全球化是指各种经济行为主体在全球范围的发展趋势，是一个描述世界变化的广度与深度的概念。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (434, 1, '经济全球化是指商品和劳务在全球范围内的自由流动。', '对', '错', '错');
INSERT INTO `judge` VALUES (435, 1, '经济全球化体现了社会化生产的要求，不仅发达国家从中受益,一些发展中国家在参与经济全球化进程中也得到了快速发展。', '对', '错', '对');
INSERT INTO `judge` VALUES (436, 1, '经济全球化也是一把“双刃剑”，它在促进经济发展的同时也带来了一些负面影响。', '对', '错', '对');
INSERT INTO `judge` VALUES (437, 1, '经济全球化意味着各种经济行为主体的竞争、冲突和合作即相互作用是在全球范围内展开的。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (438, 1, '经济全球化只会产生积极效益。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (439, 1, '科学技术革命和生产力的发展，是资本主义变化的根本推动力量。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (440, 1, '跨国公司是对外直接投资、在国外建立子公司和分支机构的大型垄断企业。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (441, 1, '垄断不能消除竞争。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (442, 1, '垄断价格的出现意味着价值规律作用的消失。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (443, 1, '垄断价格是指垄断资本家凭借其在经济上的垄断地位所规定的旨在保证其能够获得垄断利润的产品价格。它包括垄断低价和垄断高价两种基本形式。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (444, 1, '垄断利润是指垄断资本家凭借其在经济上的垄断地位而获得的大大超过平均利润的高额利润。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (445, 1, '垄断利润主要是通过规定垄断价格来实现的。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (446, 1, '垄断没有也不可能消除竞争赖以存在的客观基础。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (447, 1, '垄断时期的竞争范围则由国内扩展到国外，由经济领域扩展到政治、军事、文化等诸多领域。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (448, 1, '垄断是现代资本主义最深厚的经济基础。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (449, 1, '垄断资本的竞争双方都是实力雄厚、势均力敌的垄断组织，这便使得竞争特别激烈，更具有持久性，竞争造成的破坏也更加严重。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (450, 1, '垄断资本家实行垄断统治的主要目的，就是要凭借其在经济上的垄断地位来获得高额垄断利润。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (451, 1, '垄断资本主义条件下，经济发展中存在的两种趋势的根源是资本家追求垄断利润。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (452, 1, '垄断组织之间的竞争也始终存在。垄断组织在确立了自己的统治地位后，为了扩大统治范围，它们之间也进行着激烈的竞争。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (453, 1, '企业生产经营全球化指跨国公司在全球范围内建立分支机构，借助母公司与分支机构之间各种形式的联系，实行跨国投资和生产的过程。', '对', '错', '对');
INSERT INTO `judge` VALUES (454, 1, '社会主义制度初步显示的优越性对资本主义没有产生任何影响。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (455, 1, '生产的高度集中，在一定程度上造成了竞争的困难，从而导致垄断的趋势。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (456, 1, '生产高度集中后，也产生了垄断的必要性。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (457, 1, '资本主义生产集中发展到一定阶段，就自然而然地走向垄断。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (458, 1, '资本主义的基本矛盾具体表现为生产力的迅速增长与人民群众有支付能力的需求相对缩小的矛盾加深了。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (459, 1, '资本主义的基本矛盾具体表现为生产力社会化的高度发展同私人垄断资本盲目竞争和生产无政府状态的矛盾加剧了。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (460, 1, '生产社会化和资本主义私有制之间的矛盾，其解决的根本办法是资本主义制度被社会主义所代替。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (461, 1, '生产社会化与资本主义私人占有的矛盾，是资本主义社会一切矛盾和冲突的总根源，决定着资本主义必然灭亡的历史命运。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (462, 1, '世界市场的扩大、交通工具的完善使国与国之间联系的日益紧密，使得当下经济危机可以消除。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (463, 1, '私人股份资本所有制是一种私人资本家联合占有生产资料的所有制形式。', '对', '错', '对');
INSERT INTO `judge` VALUES (464, 1, '虽然垄断取代自由竞争而在社会经济生活中占据了统治地位，但是，它并没有消除竞争，而是凌驾于竞争之上，与竞争同时并存。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (465, 1, '所谓金融资本是指工业垄断资本和银行垄断资本溶合或混合生长而形成的一种新型资本。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (466, 1, '所谓经济全球化是指随着科学技术和国际分工的发展以及生产社会化程度的提高，使世界各国、各地区的经济活动越来越超出一国和地区的范围而相互联系和紧密结合的趋势。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (467, 1, '所谓垄断，简单地说就是“独占”，是指资本主义少数大企业为了获得高额垄断利润联合起来独占生产和市场。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (468, 1, '为了获取高额垄断利润，垄断资本家可以任意抬高垄断价格。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (469, 1, '西方国家的政治运作基本以政党为载体，执政党和反对党经常实现角色互换，“你方唱罢我登场”的钟摆效应成为常态，带来的后果是政党利益永远凌驾于国家利益之上。', '对', '错', '错');
INSERT INTO `judge` VALUES (470, 1, '西方选举制度对政治人物的考察往往主要不是着眼于治国能力，而是选举能力，以博得选民好感并能胜选为标准。', '对', '错', '对');
INSERT INTO `judge` VALUES (471, 1, '一般来说，社会主义革命发生在资本主义链条中最薄弱的环节。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (472, 1, '由美国次贷危机引发的2008年国际金融危机是自20世纪30年代大萧条以来最为严重的全球性经济危机。', '对', '错', '对');
INSERT INTO `judge` VALUES (473, 1, '由于资本主义制度存在着自身不可克服的矛盾，必然导致资本主义制度向更高社会形态的转化，因此，社会主义制度的建立就成为历史的必然。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (474, 1, '由资本主义向社会主义的过渡，是人类历史上最宏伟、景深刻的社会变革，揭示这场社会变革的必然性，研究这场社会变革的规律、特点及曲折性，是时代赋予马克思主义的崇高使命。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (475, 1, '社会主义制度初步显示的优越性对当代资本主义产生了重要影响。', '对', '错', '对');
INSERT INTO `judge` VALUES (476, 1, '在垄断时期，垄断资本家在经济上主要是依靠垄断的高压统治，在政治上仰仗政府的力量来战胜竞争对手。为了达到目的，垄断资本家有时甚至不惜采取暴力手段。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (477, 1, '在垄断统治的条件下，竞争的目的已不是取得平均利润或超额利润，而是攫取高额垄断利润。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (478, 1, '在垄断资本主义阶段，生产力的增长与生产社会化的发展进一步推动了资本社会化的发展，使私人垄断资本主义成长为国家垄断资本主义。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (479, 1, '在资本主义条件下，在表面平等的交换关系背后，是资本对劳动的实际支配和控制。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (480, 1, '资本主义必然为社会主义所代替，意味着资本主义社会将在短期内自行消亡。', '对', '错', '错');
INSERT INTO `judge` VALUES (481, 1, '资本主义创造了巨大的财富，同时又带来了社会财富的巨大浪费和破坏。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (482, 1, '资本主义的局限性在资本主义生产方式范围内是不可能根本消除的，它决定了资本主义生产方式的历史过渡性。', '对', '错', '对');
INSERT INTO `judge` VALUES (483, 1, '资本主义发展的不平衡性决定了其过渡的长期性。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (484, 1, '资本主义国家的经济制度出现多元化的趋势，公民权利有所扩大。', '对', '错', '错');
INSERT INTO `judge` VALUES (485, 1, '资本主义国家的科学，文化、教育有了巨大的发展，但同时又使社会风气腐败，精神道德颓废，社会治安恶化，这形成了显明的对照。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (486, 1, '资本主义垄断阶段，股份公司促进了资本和生产的社会化，意味着资本主义占有关系的改变。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (487, 1, '资本主义生产关系的建立，适应于生产力发展的需要。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (488, 1, '资本主义是人类社会发展过程中的特定历史阶段，其本身也经历了自由竞争资本主义和垄断资本主义两大阶段。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (489, 1, '资本主义私有制和建立在它基础上的雇佣劳动制度，是产生自己一切反面的根源。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (490, 1, '资本主义所以能在历史上起革命作用，根本原因是顺应历史发展的客观规律，打破了束缚生产力发展的腐朽的封建所有制生产关系，建立了资本主义生产关系，为生产力的发展开辟了广阔道路。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (491, 1, '资本主义提供了造福人类、解放人类的物质条件，但却利用这种物质条件破坏了人类的进一步解放，把人类又推向灾难的深渊。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (492, 1, '资本主义向社会主义的转化，是资本主义社会生产力和生产关系矛盾运动的必然结果，是资本主义社会两大对抗阶级之间阶级斗争发展的必然结局。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (493, 1, '资本主义新变化是当代不平等的国际经济关系运动的结果。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (494, 1, '资本主义新变化是发达资本主义国家对资本主义生产关系进行自我调节的结果。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (495, 1, '资本主义新变化是资本主义生产力内部矛盾运动的结果。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (496, 1, '资本主义新变化在于改变工人阶级受雇佣的阶级地位。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (497, 1, '资本主义新变化在于改变资本主义剥削的实质。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (498, 1, '资本主义新变化在于改变资本主义的基本矛盾。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (499, 1, '资本主义新变化在于改变资本主义私有制的经济基础。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (500, 1, '资本主义在繁荣进步背后，包含着日益加深的社会资本主义向更高社会形态转化的必然性。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (501, 1, '资本主义在全世界范围内被替代是一个短暂的过程。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (502, 1, '资本主义制度的建立，解放了生产力，促进厂土产力的发展和进步。但是生产力的进步是伴随着对劳动人民的剥削和压迫的加深，它成为资产阶级剥削工人、自己致富的手段和物质力量。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (503, 1, '资产阶级，由于开拓了世界市场，使一切国家的生产和消费都成为世界性的了。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (504, 1, '自由竞争引起生产集中，生产集中发展到—定阶段必然走向垄断。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (505, 1, '空想社会主义使无产阶级由自在的阶级发展为自为的阶级。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (506, 1, '越是落后的国家越容易走上社会主义道路。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (507, 1, '20世纪80年代末期和90年代初期，东欧剧变、苏联解体并不表明社会主义的失败。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (508, 1, '马克思恩格斯从来都坚持认为，无产阶级的历史地位和阶级性决定了它只能通过暴力革命来推翻资本主义统治。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (509, 1, '空想社会主义的思想是科学社会主义的直接思想来源，因为三大空想社会主义者的社会历史观基本上是唯物主义的。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (510, 1, '十月革命的胜利证实了列宁的一国胜利论。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (511, 1, '列宁对马克思主义关于无产阶级革命学说的重大贡献是一国胜利论。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (512, 1, '新经济政策的实行表明列宁的社会主义建设思想发生了重大转变。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (513, 1, '苏联模式只是特定历史条件下的产物，并不是社会主义的唯一模式。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (514, 1, '马克思主义的继承者们在实践中不断推进对于社会主义基本特征的认识。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (515, 1, '经济文化落后国家进入社会主义社会违背了生产力决定生产关系的规律。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (516, 1, '无产阶级革命夺取了政权就意味着革命的结束。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (517, 1, '社会主义从理论到实践的飞跃，是通过无产阶级革命实现的。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (518, 1, '社会主义发展的苏联模式，是社会主义的唯一模式。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (519, 1, '“所谓‘社会主义社会’不是一种一成不变的东西，而应当和任何其他社会制度一样，把它看成是经常变化和改革的社会。”（）', '对', '错', '对');
INSERT INTO `judge` VALUES (520, 1, '经济文化相对落后的国家在思想上也必然是落后的。（）', '对', '错', '错');
