INSERT INTO `judge` VALUES (528, 1, '社会主义的发展道路不是单一的，而是多样性的。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (529, 1, '同一个社会主义国家在不同的时期，可以适时地选择适合本国国情的社会主义发展道路。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (530, 1, '在当代，因为社会主义国家和资本主义国家都是既有私有制又有公有制，所以资本主义国家和社会主义国家没有什么本质的区别。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (531, 1, '马克思主义政党是工人阶级的先锋队，这是对马克思主义政党的性质所作的最简要最明确的表述。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (532, 1, '无产阶级革命是无产阶级斗争的最高形式。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (533, 1, '马克思主义政党无论是在社会主义的革命、建设或改革开放事业中，都起着领导核心的作用。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (534, 1, '社会主义从空想到科学在人类思想史上具有划时代的意义。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (535, 1, '空想社会主义是不成熟的理论。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (536, 1, '苏联模式为苏联在反法西斯战争中的胜利提供了保证。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (537, 1, '在实行战时共产主义政策时期，列宁对苏维埃俄国如何建设社会主义进行了深刻的理论思考。（）', '对', '错', '错');
