INSERT INTO `judge` VALUES (545, 1, '不同国家可以用一种模式发展社会主义。', '对', '错', '错');
INSERT INTO `judge` VALUES (546, 1, '习近平指出：“中国特色社会主义是社会主义而不是其他什么主义，科学社会主义基本原则不能丢，丢了就不是社会主义。”', '对', '错', '对');
