INSERT INTO `judge` VALUES (557, 1, '邓小平指出：“不坚持社会主义，不改革开放，不发展经济，不改善人民生活，只能是死路一条。”', '对', '错', '对');
INSERT INTO `judge` VALUES (558, 1, '以习近平为核心的党中央具有强烈的历史担当、高超的政治智慧和顽强的意志品格。', '对', '错', '对');
INSERT INTO `judge` VALUES (559, 1, '东欧一些国家因为照搬“波兰模式”造成“水土不服”引发了经济社会危机。', '对', '错', '错');
INSERT INTO `judge` VALUES (560, 1, '苏联的第一个五年计划优先发展军事工业。', '对', '错', '错');
INSERT INTO `judge` VALUES (561, 1, '列宁提出要利用资本主义建设社会主义。', '对', '错', '对');
INSERT INTO `judge` VALUES (562, 1, '苏俄和德国签订了《布列斯特合约》，退出了第一次世界大战。', '对', '错', '对');
INSERT INTO `judge` VALUES (563, 1, '战时共产主义政策的主要特征是粮食税。', '对', '错', '错');
