INSERT INTO `judge` VALUES (566, 1, '恩格斯晚年放弃了革命，转向重视合法斗争。', '对', '错', '错');
INSERT INTO `judge` VALUES (567, 1, '巴黎公社是第一国际精神的产儿，是无产阶级夺取政权的第一次伟大尝试。', '对', '错', '对');
INSERT INTO `judge` VALUES (568, 1, '1864年，国际工人协会（第一国际）应运而生。马克思是第一国际的灵魂。', '对', '错', '对');
INSERT INTO `judge` VALUES (569, 1, '《太阳城》这部空想社会主义的经典作品是法国学者马布利的作品。', '对', '错', '错');
INSERT INTO `judge` VALUES (570, 1, '圣西门提出“实业制度”，并呼吁法国统治者接受自己的改革方案。', '对', '错', '对');
INSERT INTO `judge` VALUES (571, 1, '巴黎公社主张政教分离、男女平等，实行全面的选举制和撤换制，取消高薪制。', '对', '错', '对');
INSERT INTO `judge` VALUES (572, 1, '第二国际后期，机会主义和修正主义占了上风，最终导致第二国际解体。', '对', '错', '对');
INSERT INTO `judge` VALUES (573, 1, '“剥夺剥夺者”是发生在斯大林领导苏联进行社会主义建设时期。', '对', '错', '错');
INSERT INTO `judge` VALUES (574, 1, '资本积累的一般规律是社会财富的占有呈现两极分化的趋势。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (575, 1, '商品的价值就是一般的无差别的人类劳动。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (576, 1, '具体劳动和抽象劳动是两次劳动，前者生产使用价值，后者创造商品价值。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (577, 1, '商品价格是商品价值的货币表现。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (578, 1, '货币转化为资本的根本条件是有足够的货币积累。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (579, 1, '工资是劳动的价值或价格。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (580, 1, '不变资本和可变资本的划分是以资本价值周转方式的不同来进行的。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (581, 1, '相对剩余价值是各个资本家追逐超额剩余价值的结果。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (582, 1, '社会再生产不仅是物质资料的再生产，同时又是生产关系的再生产。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (583, 1, '资本的原始积累和资本积累一样都是把剩余价值资本化。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (584, 1, '预付资本的总周转就是固定资本和流动资本的平均周转。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (585, 1, '商品本质上是有用的劳动产品。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (586, 1, '商品总是和交换分不开的。如果不是用来交换，即使是劳动产品，也不能叫商品。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (587, 1, '商品经济是以交换为目的而进行生产的经济形式。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (588, 1, '自然经济是以自给自足为特征的经济形态，生产就是直接为了满足生产者个人或经济单位的需要，而不是为了交换。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (589, 1, '存在社会分工就会产生商品经济。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (590, 1, '产品分属不同所有者是商品经济产生的决定性条件。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (591, 1, '商品经济产生于原始社会末期，至今，存在于奴隶社会、封建社会、资本主义社会和社会主义社会。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (592, 1, '使用价值是商品能够满足人们某种需要的属性，即有用性。商品的使用价值是由它的自然属性决定的。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (593, 1, '交换价值为一种使用价值同另一种使用价值相交换的量的关系或比例。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (594, 1, '形态各异的使用价值之所有能够以一定的比例互相交换，就因为在使用价值背后隐藏着一种同质的东西作为互相通约的基础，这就是价值。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (595, 1, '凝结在物品中的人类劳动就是价值。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (596, 1, '价值是商品的社会属性，它的本质是在物的外壳掩盖下的商品生产者之间相互交换劳动的关系，即生产关系。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (597, 1, '价值是交换价值的基础，交换价值是价值的表现形似。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (598, 1, '商品之所以具有二因素，是由体现在商品中的劳动二重性决定的。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (599, 1, '撇开具体形式的无差别的人类劳动叫做抽象劳动。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (600, 1, '抽象劳动是劳动的社会属性，是历史范畴，它反映的是商品生产者相互交换劳动的自然关系。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (601, 1, '具体劳动和抽象劳动是同一劳动的两个方面或两重属性，而不是两次劳动，更不是两种劳动。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (602, 1, '马克思第一次把生产商品的劳动区分为具体劳动和抽象劳动，提出劳动二重性的学说，从而把劳动价值论建立在科学基础上。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (603, 1, '商品价值量由生产商品的劳动时间决定。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (604, 1, '社会必要劳动时间还决定商品购买者的命运（）', '对', '错', '错');
INSERT INTO `judge` VALUES (605, 1, '简单劳动，就是不需过任何专门培养和训练，一个健全的人都能从事的劳动。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (606, 1, '复杂劳动，就是指需要经过专门培养和训练，具有一定知识和技能才能胜任的劳动。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (607, 1, '商品价值量由生产这种商品的社会必要劳动时间决定。但是社会必要劳动时间并不是固定不变的，它随着各部门大多数生产者的劳动生产率的变化而变化，因此单位商品价值量与劳动生产率的变化有着密切的关系。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (608, 1, '劳动生产率和商品的使用价值总量成正比例，同单位商品的价值量成反比。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (609, 1, '复杂劳动等于自乘的或多倍的简单劳动。', '对', '错', '对');
INSERT INTO `judge` VALUES (610, 1, '商品的交换是以货币为媒介的。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (611, 1, '在以私有制为基础的商品经济条件下，复杂劳动转化为简单劳动是在商品交换过程中自发实现的。', '对', '错', '对');
INSERT INTO `judge` VALUES (612, 1, '货币的出现解决了商品经济的基本矛盾，即私人劳动和社会劳动的矛盾（）', '对', '错', '错');
INSERT INTO `judge` VALUES (613, 1, '货币的出现，它使商品世界分裂为对立的两极：一极是商品，它们是各种特殊的使用价值，要求转化为价值；一极是货币，是价值的化身。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (614, 1, '货币就是固定充当一般等价物的特殊商品，体现着商品生产者之间的经济关系。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (615, 1, '形成商品价值量的劳动是以复杂劳动为尺度的。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (616, 1, '马克思把商品转换成货币称为“商品的惊险的跳跃”。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (617, 1, '私人劳动和社会劳动的矛盾决定着商品经济的本质及发展过程.（）', '对', '错', '对');
INSERT INTO `judge` VALUES (618, 1, '在私有制商品经济条件下，商品的使用价值和价值之间的矛盾是由生产商品的私人劳动和社会劳动之间的矛盾决定的。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (619, 1, '价值规律存在的客观前提是商品经济，只要有商品生产、商品交换，价值规律就必然存在并发生作用。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (620, 1, '价值规律是商品经济的基本规律，市场机制发挥其功能的基础，市场机制是价值规律得以贯彻的形式。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (621, 1, '商品的价值量由生产商品的社会必要劳动时间决定，商品以价值为基础，按照价值量进行交换，即等价交换。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (622, 1, '在市场经济条件下，价值规律作用的基本形式是价值以价格为中心上下波动。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (623, 1, '价格虽然经常随供求变化而时涨时落，但并不会脱离价值这根轴线，它总是围绕价值自发地上下波动。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (624, 1, '从局部或短期看，价格可能低于或高于价值，但从一个较长时期或全社会看，商品的总价格和总价值仍然是相等的。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (625, 1, '价格以价值为基础，自发地围绕价值上下波动，不但不是对价值规律的否定，恰恰是价值规律在市场经济条件下发生作用的表现形式。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (626, 1, '价值规律贯穿于商品经济的全部过程，它既支配商品生产，又支配商品流通。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (627, 1, '资本最初都表现为一定数量的货币,所以货币就是资本。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (628, 1, '资本主义经济制度的形成是以劳动力成为商品为前提条件的。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (629, 1, '劳动力成为商品是货币转化为资本的前提。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (630, 1, '劳动力成为商品，标志着简单商品生产发展到资本主义商品生产的新阶段。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (631, 1, '劳动力的价值，是由生产、发展、维持和延续劳动力所必需的生活必需品的价值决定的。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (632, 1, '劳动力商品的特殊性主要是体现在它的使用价值上，这是劳动力商品关键。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (633, 1, '劳动力成为商品是货币转化为资本的前提，也是剩余价值产生的最重要的、具有决定意义的前提条件。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (634, 1, '资本主义生产过程是劳动过程和价值形成过程的统一。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (635, 1, '剩余价值是雇佣工人所创造的并被资本家无偿占有的超过劳动力价值的那部分价值，它是雇佣工人剩余劳动的凝结，体现了资本家与雇佣工人之间剥削与被剥削的关系。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (636, 1, '在整个资本中，不变资本并不能创造价值增殖，只有可变资本，才能创造价值增殖，才是剩余价值产生的唯一源泉。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (637, 1, '剩余价值和不变资本的比率叫做剩余价值率。剩余价值率也叫剥削率。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (638, 1, '超额剩余价值是个别资本家由于提高劳动生产率，使其生产的商品的个别价值低于社会价值，从而获得的更多的剩余价值。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (639, 1, '超额剩余价值，实际上也是一种特殊形式的相对剩余价值。它属于相对剩余价值。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (640, 1, '缩短必要劳动时间是通过全社会劳动生产率的提高实现的。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (641, 1, '资本积累是资本主义扩大再生产的源泉。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (642, 1, '资本主义简单再生产就其实质而言，是物质资料再生产和资本主义生产关系再生产的统一。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (643, 1, '资本积累的本质，就是资本家不断地利用无偿占有的工人创造的剩余价值来扩大自己的资本规模，进一步扩大和加强对工人的剥削和统治。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (644, 1, '在资本主义生产过程中，资本有机构成的提高是一般趋势，这是由资本的本性决定的。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (645, 1, '资本有机构成提高的前提是可变资本增大。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (646, 1, '个别资本的增大是通过资本积聚和资本集中这两种形式来实现的。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (647, 1, '货币资本是指以货币形式存在的资本，其职能就是在资本循环的购买阶段购买生产资料和劳动力商品，为生产剩余价值作准备。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (648, 1, '生产资本，是指以生产资料和劳动力形式存在处于生产阶段的资本，其职能是将机器、厂房、原材料等生产资料和劳动力相结合，生产出带有剩余价值的新商品。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (649, 1, '商品资本，指的就是以商品形式存在的资本，其职能是通过售卖，把包含剩余价值的新商品转化为货币，实现资本的价值和剩余价值。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (650, 1, '资本积聚是指个别资本通过剩余价值的资本化来增大资本的总量。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (651, 1, '资本不仅是带来剩余价值的价值，体现着一种生产关系，而且还是一种运动，是一个通过不同阶段的循环运动过程。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (652, 1, '产业资本的连续循环是流通过程和生产过程的统一。', '对', '错', '对');
INSERT INTO `judge` VALUES (653, 1, '生产资料的生产和消费资料的生产既要保证简单再生产的实现，更要保证扩大再生产的实现。', '对', '错', '对');
INSERT INTO `judge` VALUES (654, 1, '使社会总产品在价值上得到补偿，在实物上得到替换,以使社会再生产顺利进行，是不以人的意志为转移的客观规律。', '对', '错', '对');
INSERT INTO `judge` VALUES (655, 1, '资本周转速度决定年剩余价值率的高低。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (656, 1, '资本积累的历史趋势是资本主义制度的必然灭亡和社会主义制度的必然胜利。', '对', '错', '对');
INSERT INTO `judge` VALUES (657, 1, '利润是剩余价值的转化形式。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (658, 1, '利润转化为平均利润，是剩余价值规律和竞争规律作用的必然结果，体现着不同部门的资本家集团要求按照等量资本获得等量利润的原则来瓜分剩余价值的关系。', '对', '错', '对');
INSERT INTO `judge` VALUES (659, 1, '平均利润率规律的作用表明，平均利润率是剩余价值总量与社会总资本的比率。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (660, 1, '产业资本家和商业资本家都按平均利润率获得产业利润和商业利润。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (661, 1, '利润转化为平均利润，价值也就转化为生产价格。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (662, 1, '生产价格是商品价值的转化形式，是生产成本与剩余价值之和。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (663, 1, '生产价格随着商品价值的变化而变动，生产商品的社会必要劳动时间减少，生产价格就会降低；反之，生产价格就会提高。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (664, 1, '资本主义民主制度是与资本主义生产方式相适应而发展起来的。', '对', '错', '对');
INSERT INTO `judge` VALUES (665, 1, '资本主义政治制度本质上是资产阶级进行政治统治和社会管理的手段和方式，是为资产阶级专政服务的。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (666, 1, '生产剩余价值，是资本主义生产方式的绝对规律。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (667, 1, '价值规律决定资本主义生产的实质是生产价值，表明资本主义生产的动力和目的是追求尽可能多的价值。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (668, 1, '在资本主义生产自动化的条件下，剩余价值的源泉仍然只能是雇佣工人的剩余劳动，因而生产剩余价值仍然是资本主义生产方式的绝对规律。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (669, 1, '不变资本是以生产资料形态存在的资本。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (670, 1, '生产成本是由生产中实际耗费的不变资本和可变资本所构成的。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (671, 1, '剩余价值理论是马克思主义经济理论的基石，是无产阶级反对资产阶级、揭示资本主义制度剥削本质的锐利武器。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (672, 1, '由于唯物史观和剩余价值的发现，社会主义由空想变为科学。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (673, 1, '社会总产品在价值形态上又叫社会总价值。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (674, 1, '个别资本通过结合而形成较大的资本是资本积聚。（）', '对', '错', '错');
INSERT INTO `judge` VALUES (675, 1, '劳动力商品在使用价值上有一个很大的特点，就是它的使用价值是价值的源泉。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (676, 1, '资本是增殖价值的价值，它反映了资本家和雇佣工人之间剥削和被剥削的关系。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (677, 1, '可变资本的价值在生产过程中不是被转移到新产品中去，而是由工人的劳动再生产出来。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (678, 1, '生产剩余价值是资本主义生产方式的绝对规律。（）', '对', '错', '对');
INSERT INTO `judge` VALUES (679, 1, '价值规律是在市场配置资源的过程中体现它的客观要求和作用的。', '对', '错', '对');
INSERT INTO `judge` VALUES (680, 1, '价值规律贯穿于商品经济的全部过程，它既支配商品生产，又支配商品流通。', '对', '错', '对');
INSERT INTO `judge` VALUES (681, 1, '社会主义经过长期的发展，在高度发达的基础上，最终将走向共产主义。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (682, 1, '马克思和恩格斯展望未来社会的方法是科学的。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (683, 1, '马克思恩格斯站在无产阶级的立场上，运用科学的方法，致力于研究人类社会特别是资本主义社会，第一次揭示了社会发展的一般规律和资本主义社会发展的特殊规律。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (684, 1, '把握现在和展望未来的向导是认为人类社会的发展具有客观规律，科学揭示这些规律。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (685, 1, '马克思、恩格斯关于未来社会的预测，是在科学地批判和剖析资本主义社会的过程中作出的。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (686, 1, '马克思、恩格斯对资本主义批判的高明之处，在于他们不是只看到资本主义社会的弊端，而是进一步揭示出弊端的根源，揭示出资本主义发展中自我否定的力量，发现资本主义的矛盾运动中孕育着的新社会因素，并以此作出对未', '对', '错', 'A');
INSERT INTO `judge` VALUES (687, 1, '预见未来共产主义社会的基本特征，既从资本主义社会中寻找灵感，更从社会主义社会的建设与发展中寻找启示。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (688, 1, '共产主义社会是人类社会的最高阶段。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (689, 1, '马克思、恩格斯生活的年代还没有社会主义社会，他们只能通过分析考察资本主义社会及其运动规律来求得预见未来社会的启示。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (690, 1, '马克思、恩格斯在展望未来社会时，总是只限于指出未来社会发展的方向、原则和基本特征，而把具体情形留给后来的实践去回答。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (691, 1, '“我们坚信马克思主义关于人类社会必然走向共产主义这一基本原理。……我们对社会未来发展的方向可以作出科学上的预见，但未来的事情具体如何发展，应该由未来的实践去回答。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (692, 1, '马克思对共产主义社会做了详尽的描述。（）', '对', '错', 'B');
INSERT INTO `judge` VALUES (693, 1, '马克思在分析共产主义的特征时并没有参考资本主义制度。（）', '对', '错', 'B');
INSERT INTO `judge` VALUES (694, 1, '当代中国共产党人对共产主义的看法是，“共产主义社会，将是物质财富极大丰富，人民精神境界极大提高，每个人自由而全面发展的社会”。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (695, 1, '社会生产力高度发展，产品极大丰富，是实现共产主义的必要条件。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (696, 1, '马克思在《哥达纲领批判》中指出，只有在生产力增长起来，在集体财富的一切源泉都充分涌流之后，才能实行共产主义“各尽所能，按需分配”的分配原则。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (697, 1, '生产力的高度发展又是共产主义社会本身的一个重要特征。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (698, 1, '适应高度发展的社会化大生产的需要，共产主义社会在生产关系上将废除私有制，实行普遍的生产资料公有制。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (699, 1, '《共产党宣言》中有一句名言：“共产党人可以把自己的理论概括为一句话：消灭私有制。”（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (700, 1, '与生产资料的社会占有相适应，共产主义社会将按照自然资源的情况和社会成员的需要，对生产进行有计划的组织和管理。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (701, 1, '在共产主义社会，个人消费品的分配方式是“各尽所能，按需分配”。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (702, 1, '按需分配是共产主义社会的分配原则。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (703, 1, '共产主义是一种根据资本主义发展规律基础上建立起来的乌托邦。（）', '对', '错', 'B');
INSERT INTO `judge` VALUES (704, 1, '按劳分配是共产主义社会的分配原则。（）', '对', '错', 'B');
INSERT INTO `judge` VALUES (705, 1, '只有到了共产主义社会，人类社会的分配方式才能突破按劳分配的局限，根据人们的需要进行生活资料的分配,从而实现分配的真正平等。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (706, 1, '在共产主义社会，阶级将会消亡。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (707, 1, '阶级是在社会发展到一定阶段上产生的现象，它的产生虽然是社会发展的结果，但却使人类社会陷入大规模的内部分裂和纷争之中。', '对', '错', 'A');
INSERT INTO `judge` VALUES (708, 1, '到共产主义社会，阶级消灭了，阶级剥削和压迫不复存在，阶级斗争也随之消失。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (709, 1, '在共产主义社会不存在剥削和阶级差别。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (710, 1, '在共产主义社会，国家也将消亡，作为阶级压迫工具的军队、警察、监狱等将失去作用。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (711, 1, '体脑差别在社会主义阶段就会被消除。（）', '对', '错', 'B');
INSERT INTO `judge` VALUES (712, 1, '共产主义社会的和谐只是人与人之间的和谐。（）', '对', '错', 'B');
INSERT INTO `judge` VALUES (713, 1, '在共产主义社会，战争将不复存在。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (714, 1, '在共产主义社会中，由于私有制和利益对立的消除，由于旧式分工的消除和人的全面发展，工业与农业、城市与乡村、脑力劳动与体力劳动的对立归于消失。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (715, 1, '在共产主义社会，不仅社会是和谐的，而且社会与自然之间也将达成和谐。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (716, 1, '人们的精神境界得到极大提高。这是共产主义新人的重要体现。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (717, 1, '到共产主义社会，人们不仅具有多方面的才能，而且具有高度的觉悟和髙尚的道德品质，乐意为社会公共事业作出贡献已经成为人的本能。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (718, 1, '实现人的自由而全面的发展，是马克思主义追求的根本价值目标,也是共产主义社会的根本特征。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (719, 1, '在共产主义社会，代替那存在着阶级和阶级对立的资产阶级旧社会的，将是这样一个联合体，在那里，每个人的自由发展是一切人的自由发展的条件。', '对', '错', 'A');
INSERT INTO `judge` VALUES (720, 1, '人的全面发展是指人的各种要求都能得到满足。（）', '对', '错', 'B');
INSERT INTO `judge` VALUES (721, 1, '在共产主义社会，人的发展是自由而全面的发展，是建立在个体高度自由自觉基础上的全面发展。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (722, 1, '人的发展是全面的发展，不仅体力和智力得到发展，各方面的才能和工作能力得到发展，而且人的社会联系和社会交往也得到发展。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (723, 1, '共产主义社会里仍然会有分工，但这是自觉的新式的分工，不再是生产者全面发展的限制。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (724, 1, '在社会主义社会，劳动是一种娱乐。（）', '对', '错', 'B');
INSERT INTO `judge` VALUES (725, 1, '在共产主义社会，这个劳动时间将会大大缩短。人们只需要从事较少时间的劳动，就能为社会创造出足够的物质财富。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (726, 1, '在自由王国里人们可以拥有不受限制的自由。（）', '对', '错', 'B');
INSERT INTO `judge` VALUES (727, 1, '在共产主义社会，劳动不再是单纯的谋生手段，而成为“生活的第一需要”。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (728, 1, '共产主义一定能够实现，这是由人类社会的发展规律所决定的。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (729, 1, '人类社会从低级到高级的发展，是一个社会形态发展和交替的过程。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (730, 1, '奴隶社会取代原始社会，封建社会取代奴隶社会，资本主义社会取代封建社会，社会主义社会取代资本主义社会，社会主义社会经过长期发展进入共产主义社会，这是一个客观必然的历史进程。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (731, 1, '共产主义理想是能够实现的理想，它与一切空想和幻想有着本质区别。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (732, 1, '共产主义理想作为一种社会理想，是在对人类社会发展规律认识的基础上设想的社会发展目标。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (733, 1, '我们可以根据对社会结构的认识，从生产力状况、生产关系状况、社会生活和精神生活等方面去把握共产主义社会的基本特征。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (734, 1, '我们完全可以根据历史规律和历史趋势不断加深对共产主义理想的轮廓和基本特征的认识。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (735, 1, '共产主义理想的实现不是靠什么神秘的力量或奇迹，而是靠社会的发展和进步，靠人民群众的实践。', '对', '错', 'A');
INSERT INTO `judge` VALUES (736, 1, '共产主义理想是无法实现的社会理想，就是马克思说说而已。（）', '对', '错', 'B');
INSERT INTO `judge` VALUES (737, 1, '20世纪社会主义运动中的苏东剧变，说明共产主义理想不对头。（）', '对', '错', 'B');
INSERT INTO `judge` VALUES (738, 1, '马克思对共产主义的想象是一种不切实际的幻想。（）', '对', '错', 'B');
INSERT INTO `judge` VALUES (739, 1, '共产主义理想一定会实现，是以人类社会发展规律以及资本主义社会的基本矛盾发展为依据的。', '对', '错', 'A');
INSERT INTO `judge` VALUES (740, 1, '社会主义运动的实践，特别是社会主义国家的兴起和不断发展，已经并正在用事实证明共产主义理想实现的必然性。', '对', '错', 'A');
INSERT INTO `judge` VALUES (741, 1, '从一定意义上讲,社会主义革命的胜利本身就是对共产主义理想可以实现的证明。', '对', '错', 'A');
INSERT INTO `judge` VALUES (742, 1, '社会主义社会是共产主义社会的初级阶段，它与共产主义社会具有根本性质上的一致性。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (743, 1, '社会发展的规律是在人们的活动中形成的，是人们社会活动的规律，它的实现和发挥作用离不开人们的社会活动，特别是离不开人们自觉创造历史的活动。', '对', '错', 'A');
INSERT INTO `judge` VALUES (744, 1, '实现共产主义理想是少数精英人物愿望。（）', '对', '错', 'B');
INSERT INTO `judge` VALUES (745, 1, '实现共产主义，必须靠优秀的大人物作为依靠力量。（）', '对', '错', 'B');
INSERT INTO `judge` VALUES (746, 1, '无产阶级不是先进生产力的代表，受剥削是正常的，靠他们实现共产主义不靠谱。（）', '对', '错', 'B');
INSERT INTO `judge` VALUES (747, 1, '在共产主义实现的历史必然性中就包含着无产阶级和广大人民群众对共产主义理想的追求。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (748, 1, '实现共产主义理想是广大人民群众的共同愿望。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (749, 1, '在历史上，人民群众对美好生活和理想社会的向往和追求源远流长，总体上说是向往和追求一种没有剥削、没有压迫的理想社会。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (750, 1, '实现共产主义，必须找到现实的阶级力量，这就是现代工人阶级或无产阶级。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (751, 1, '工人阶级的解放和全人类的解放是完全一致的。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (752, 1, '无产阶级是先进生产力的代表，深受资产阶级的剥削和压迫，为争取自身的解放进行了不懈的斗争。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (753, 1, '马克思主义的创立及其与工人运动的结合，特别是马克思主义政党的产生，使无产阶级有了科学的理论指导和坚强的领导核心，走上了实现自身历史使命的更加自觉的道路。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (754, 1, '无产阶级的解放与全人类的解放是完全一致的。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (755, 1, '共产主义的实现有些情况十分漫长，有些情况非常简单，说不定明天就实现了。（）', '对', '错', 'B');
INSERT INTO `judge` VALUES (756, 1, '争取共产主义社会制度的最终实现，不仅是无产阶级彻底解放的标志，也是全人类得到解放的根本要求和体现。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (757, 1, '共产主义的实现是一个十分漫长而且充满艰难曲折的历史过程。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (758, 1, '实现共产主义必须经历许多历史阶段。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (759, 1, '资本主义从兴盛走向衰落和灭亡需要相当长的历史时期。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (760, 1, '从资本主义到社会主义有一个过渡时期，这是一个充满矛盾和斗争的复杂历史过程。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (761, 1, '共产主义社会的第一阶段即社会主义社会是一个长期的历史过程，特别是从不发达的社会主义到发达的社会主义，更有一个长期发展的过程。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (762, 1, '从发达的社会主义向共产主义的转变和过渡，也需要一定的历史时期。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (763, 1, '资本主义作为一个社会形态，其走向灭亡是一个长期的历史过程。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (764, 1, '资本主义马上就完蛋了，坚定信心跑步进入共产主义社会。（）', '对', '错', 'B');
INSERT INTO `judge` VALUES (765, 1, '既然一国或少数国家可以实现社会主义，那么，一国或少数国家也可以实现共产主义。（）', '对', '错', 'B');
