INSERT INTO `judge` VALUES (770, 1, '在全世界实现共产主义，首先将取决于社会主义国家的巩固和发展，取决于这些国家所经历的社会主义建设的历史进程。', '对', '错', 'A');
INSERT INTO `judge` VALUES (771, 1, '共产主义只有在社会主义社会充分发展和高度发达的基础上才能实现。（）', '对', '错', 'A');
