INSERT INTO `judge` VALUES (773, 1, '社会主义社会在自身的发展中也会经历从低级到高级的发展阶段，在一切条件具备之后才能达到共产主义社会。（）', '对', '错', 'A');
INSERT INTO `judge` VALUES (774, 1, '我国现在尚处在社会主义社会的初级阶段，对于整个社会主义时期究竟会有多长，究竟要经历哪些发展阶段，何时才能达到共产主义社会，还需要随着历史的发展进一步认识和探索。（）', '对', '错', 'A');
